            array ('campo' => 'pais_nomb', 'nombre' => 'Nombre del p&aacute;is', 'tipo_objeto' => 'input_text', 'tipo_dato' => 'cleartext'), 
            array ('campo' => 'pais_estd', 'nombre' => 'Estado del registro', 'tipo_objeto' => 'input_radio', 'tipo_dato' => 'bool', 'opcion0' => 'Inactivo', 'opcion1' => 'Activo'), 
