                        array('nombre' => 'ISO_3166-1 de dos car&aacute;cteres', 'width' => '', 'campo' => 'pais_iso2', 'formato' => 'normal'), 
                        array('nombre' => 'ISO_3166-1 de tres car&aacute:cteres', 'width' => '', 'campo' => 'pais_iso3', 'formato' => 'normal'), 
                        array('nombre' => 'Nombre', 'width' => '', 'campo' => 'pais_nomb', 'formato' => 'normal'), 
