(function () {
    $.fn.foggle = function (a) {
        var b = {};
        var c = $.extend({}, a, b);
        selector = this;
        $("." + c.targetClass).each(function () {
            $(this).attr("disabled", "disabled")
        });
        $(selector).bind("click change", c, handler)
    };

    function handler(a) {
        var b;
        if ($(this).is("select")) {
            b = $(this).find('option:selected').val()
        } else {
            b = this.value
        } if (b == a.data.acceptVal) {
            if ($(this).is("input[type='checkbox']") && !$(this).is(":checked")) {
                $("." + a.data.targetClass).each(function () {
                    $(this).not(':button, :submit, :reset, :hidden').val('').attr("disabled", "disabled").removeAttr('checked').removeAttr('selected')
                })
            } else {
                $("." + a.data.targetClass).each(function () {
                    $(this).removeAttr("disabled")
                })
            }
        } else {
            $("." + a.data.targetClass).each(function () {
                $(this).not(':button, :submit, :reset, :hidden').val('').attr("disabled", "disabled").removeAttr('checked').removeAttr('selected')
            })
        }
    }
})(jQuery);